script:
frappe.ui.form.on('Purchase Receipt', {
    refresh: function(frm) {
        frm.add_custom_button(__('Customs Card Receipt'), function() {
            frappe.new_doc('Customs Card Receipt', {
                // You can pass default values here if needed
                // purchase_receipt: frm.doc.name  (if you want to link it back to the purchase receipt)
            });
        }, __("Create"));
    }
});

frappe.ui.form.on('Customs Card Receipt', {
    refresh: function (frm) {
        // Add a custom button in the Purchase Receipt
        frm.add_custom_button(__('Create Customs Card Receipt'), function () {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Purchase Receipt',
                    name: frm.doc.name
                },
                callback: function (r) {
                    if (r.message) {
                        let purchase_receipt_name = r.message.name;
                        frappe.model.open_mapped_doc({
                            method: 'path.to.your.method',
                            frm: frm,
                            purchase_receipt_name: purchase_receipt_name
                        });
                    }
                }
            });
        });
    }
});
